<?php require_once './includes/appvars.inc.php';$title = 'Member Map Locations';include_once './includes/session.inc.php';// login url for login script$login_url = 'http://' . $_SERVER['HTTP_HOST'] . dirname($_SERVER['PHP_SELF']) . '/login.php';   // Check for login status, if not logged in then go to login scriptif (!isset($_SESSION['username']) || !isset($_SESSION['user_id'])) {    header('Location: ' . $login_url);    exit();}?><!DOCTYPE HTML><html>    <head>    <meta charset="UTF-8">    <title><?php echo $title; ?></title>    <link rel="stylesheet" type="text/css" href="css/normalize.css">    <link rel="stylesheet" type="text/css" href="css/bootstrap.css">    <link rel="stylesheet" type="text/css" href="css/main.css">    <link rel="stylesheet" type="text/css" href="css/bootstrap-responsive.css">        <script type="text/javascript" src="http://maps.googleapis.com/maps/api/js?sensor=false"></script>    <script type="text/javascript">    //<![CDATA[    var customIcons = {      blue: {        icon: 'http://labs.google.com/ridefinder/images/mm_20_blue.png'      },      red: {        icon: 'http://labs.google.com/ridefinder/images/mm_20_red.png'      }    };    function load() {      var map = new google.maps.Map(document.getElementById("map"), {        center: new google.maps.LatLng(40.874439, -124.079102),        zoom: 5,        mapTypeId: 'roadmap'      });      var infoWindow = new google.maps.InfoWindow;      // Change this depending on the name of your PHP file      downloadUrl("genxml.php", function(data) {        var xml = data.responseXML;        var markers = xml.documentElement.getElementsByTagName("marker");        for (var i = 0; i < markers.length; i++) {          var firstName = markers[i].getAttribute("firstName");          var lastName = markers[i].getAttribute("lastName");          var address = markers[i].getAttribute("address");          //var type = markers[i].getAttribute("type");          var point = new google.maps.LatLng(              parseFloat(markers[i].getAttribute("lat")),              parseFloat(markers[i].getAttribute("lng")));          var html = "<b>" + firstName + " " + lastName + "</b> <br/>" + address;          var icon = customIcons['red'];          var marker = new google.maps.Marker({            map: map,            position: point,            icon: icon.icon          });          bindInfoWindow(marker, map, infoWindow, html);        }      });    }    function bindInfoWindow(marker, map, infoWindow, html) {      google.maps.event.addListener(marker, 'click', function() {        infoWindow.setContent(html);        infoWindow.open(map, marker);      });    }    function downloadUrl(url, callback) {      var request = window.ActiveXObject ?          new ActiveXObject('Microsoft.XMLHTTP') :          new XMLHttpRequest;      request.onreadystatechange = function() {        if (request.readyState == 4) {          request.onreadystatechange = doNothing;          callback(request, request.status);        }      };      request.open('GET', url, true);      request.send(null);    }    function doNothing() {}    //]]>  </script>  </head>  <body onload="load()">    <?php        require_once './includes/nav.inc.html.php';                  ?>    <div id="mainContent">      <div class="span12">        <button class="btn-success btn pull-right" id="logout" data-sid="<?php echo $sid; ?>">Log Out</button>        <p class="pull-right">Hello, <?php echo $_SESSION['username'] ?>&nbsp;&nbsp;</p>        <h1>Little League Member Map Locations</h1>         <div id="map" style="width: 800px; height: 500px"></div>      </div>      <div class="clearfix"></div>    </div>            <?php require_once './includes/footer.inc.html.php';?>