<?php require_once './includes/appvars.inc.php';$title = 'Baseball Skills Videos';include_once './includes/session.inc.php';// login url for login script$login_url = 'http://' . $_SERVER['HTTP_HOST'] . dirname($_SERVER['PHP_SELF']) . '/login.php';   // Check for login status, if not logged in then go to login scriptif (!isset($_SESSION['username']) || !isset($_SESSION['user_id'])) {    header('Location: ' . $login_url);    exit();}require_once './includes/header.inc.html.php';?>    <div id="mainContent">  <div class="span12">      <button class="btn-success btn pull-right" id="logout" data-sid="<?php echo $sid; ?>">Log Out</button>      <p class="pull-right">Hello, <?php echo $_SESSION['username'] ?>&nbsp;&nbsp;</p>      <h1>Baseball Skills Videos</h1>        <?php       define('YOUTUBE_URL', 'http://gdata.youtube.com/feeds/api/videos?q=important+baseball+skills&max-results=10');            define('NUM_VIDEOS', 5);          // Read the XML data into an object      $xml = simplexml_load_file(YOUTUBE_URL);          $num_videos_found = count($xml->entry);      if ($num_videos_found > 0) {        echo '<ul class="articleList">';        for ($i = 0; $i < min($num_videos_found, NUM_VIDEOS); $i++) {          // Get the title          $entry = $xml->entry[$i];          $media = $entry->children('http://search.yahoo.com/mrss/');          $title = $media->group->title;              // Get the duration in minutes and seconds, and then format it          $yt = $media->children('http://gdata.youtube.com/schemas/2007');          $attrs = $yt->duration->attributes();          $length_min = floor($attrs['seconds'] / 60);          $length_sec = $attrs['seconds'] % 60;          $length_formatted = $length_min . (($length_min != 1) ? ' minutes, ':' minute, ') .            $length_sec . (($length_sec != 1) ? ' seconds':' second');              // Get the video URL          $attrs = $media->group->player->attributes();          $video_url = $attrs['url'];              // Get the thumbnail image URL          $attrs = $media->group->thumbnail[0]->attributes();          $thumbnail_url = $attrs['url'];               // Display the results for this entry          echo '<li><a href="' . $video_url . '" target="_blank"><img src="' . $thumbnail_url . '" /></a>            <p><a href="' . $video_url . '" target="_blank">'.$title .'</a><br />            <small>' . $length_formatted . '</small></p>            <div class="clearfix"></div></li>';                 }        echo '</ul>';      }      else {        echo '<p>Sorry, no videos were found.</p>';      }      ?>    </div>    <div class="clearfix"></div></div><?php require_once './includes/footer.inc.html.php';?>